Выборка = РезультатЗапроса.Выбрать();

Пока Выборка.Следующий() Цикл

	НоваяПозиция = Результат.Добавить();

	НоваяПозиция.Ссылка = Выборка.Ссылка;

	НоваяПозиция.НомСтр = Формат(Выборка.НомерСтроки, "ЧГ=0");
	НоваяПозиция.НаимТов = СокрЛП(Выборка.НаименованиеНоменклатуры);

	НоваяПозиция.КодТов = Выборка.ШтрихкодНоменклатурыКонтрагента;

	КодПроисх   = ВРег(СокрЛП(Выборка.КодПроисх));
	КодПроисх   = ?(ПустаяСтрока(КодПроисх), "643", КодПроисх);
	НоваяПозиция.КодПроисх = КодПроисх;

	КрНаимСтрПр = ВРег(СокрЛП(Выборка.КрНаимСтрПр));
	КрНаимСтрПр = ?(ПустаяСтрока(КрНаимСтрПр), "RU", КрНаимСтрПр);
	НоваяПозиция.КрНаимСтрПр = КрНаимСтрПр;

	Если НЕ КрНаимСтрПр = "RU" Тогда
		НоваяПозиция.НомГТД = Выборка.НомерГТД;
	КонецЕсли;

	Если Выборка.СтавкаНДС = Перечисления.СтавкиНДС.БезНДС Тогда
		НалСт = "без НДС";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС10 Тогда
		НалСт = "10%";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС10_110 Тогда
		НалСт = "10/110";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС18 Тогда
		НалСт = "18%";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС18_118 Тогда
		НалСт = "18/118";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС20 Тогда
		НалСт = "20%";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС20_120 Тогда
		НалСт = "20/120";
	Иначе
		НалСт = "НДС исчисляется налоговым агентом";
	КонецЕсли;

	НоваяПозиция.НалСт = НалСт;

	НоваяПозиция.ОКЕИ_Тов = СокрЛП(Выборка.ОКЕИ_Тов);  //Может ли ОКЕИ корректироваться?
	НоваяПозиция.КолТов = Формат(Выборка.Количество, "ЧДЦ=3; ЧРД=.; ЧГ=0; ЧН=");

	// Учитываем скидки (опционально)
	//Если ЗначениеЗаполнено(Выборка.ОбщаяСкидка) И Выборка.ЭтоКорректировка Тогда
	//	знЦена            = Окр(Выборка.Цена - Выборка.Цена * (Выборка.ОбщаяСкидка / 100), 2);
	//	знЦенаДоИзменения = Окр(Выборка.ЦенаДоИзменения - Выборка.ЦенаДоИзменения * (Выборка.ОбщаяСкидка / 100), 2);
	//	Сумма             = Окр(Выборка.Сумма - Выборка.Сумма * (Выборка.ОбщаяСкидка / 100), 2);
	//	СуммаНДС          = Окр(Выборка.СуммаНДС - Выборка.СуммаНДС * (Выборка.ОбщаяСкидка / 100), 2);
	//ИначеЕсли ЗначениеЗаполнено(Выборка.ОбщаяСкидка) Тогда
	//	знЦена            = Окр(Выборка.Цена - Выборка.Цена * (Выборка.ОбщаяСкидка / 100), 2);
	//	знЦенаДоИзменения = Окр(Выборка.ЦенаДоИзменения - Выборка.ЦенаДоИзменения * (Выборка.ОбщаяСкидка / 100), 2);
	//КонецЕсли;

	Если Выборка.СуммаВключаетНДС Тогда
		СтТовБезНДС = Выборка.Сумма - Выборка.СуммаНДС;
		ЦенаБезНДС  = Окр((Выборка.Цена * 100)/(100 + СтавкаНДСЧислом(Выборка.СтавкаНДС)), 2);
		СтТовУчНал = Выборка.Сумма;
	Иначе
		СтТовБезНДС = Выборка.Сумма;
		ЦенаБезНДС = Выборка.Цена;
		СтТовУчНал = Выборка.Сумма + Выборка.СуммаНДС;
	КонецЕсли;

	НоваяПозиция.СтТовБезНДС = Формат(СтТовБезНДС, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	НоваяПозиция.СтТовУчНал = Формат(СтТовУчНал, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");

	Если НЕ Выборка.СтавкаНДС = Перечисления.СтавкиНДС.БезНДС Тогда
		НоваяПозиция.СумНал = Формат(Выборка.СуммаНДС, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	Иначе
		НоваяПозиция.СумНалБезНДС = "без НДС";
	КонецЕсли;

	НоваяПозиция.ЦенаТов = Формат(ЦенаБезНДС, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");

