	Если Выборка.СуммаВключаетНДС Тогда
		СтТовБезНДССтоимДоИзм    = Выборка.СуммаДоИзменения - Выборка.СуммаНДСДоИзменения;
		СтТовБезНДССтоимПослеИзм = Выборка.Сумма - Выборка.СуммаНДС;
		ЦенаБезНДСПосле          = Окр((Выборка.Цена * 100)/(100 + СтавкаНДСЧислом(Выборка.СтавкаНДС)), 2);
		ЦенаБезНДСДо   	         = Окр((Выборка.ЦенаДоИзменения * 100)/(100 + СтавкаНДСЧислом(Выборка.СтавкаНДС)), 2);
		СтТовУчНалСтоимДоИзм     = Выборка.СуммаДоИзменения;
		СтТовУчНалСтоимПослеИзм  = Выборка.Сумма;
	Иначе
		СтТовБезНДССтоимДоИзм = Выборка.СуммаДоИзменения;
		СтТовБезНДССтоимПослеИзм = Выборка.Сумма;
		ЦенаБезНДСПосле = Выборка.Цена;
		ЦенаБезНДСДо = Выборка.ЦенаДоИзменения;
		СтТовУчНалСтоимДоИзм = Выборка.СуммаДоИзменения + Выборка.СуммаНДСДоИзменения;
		СтТовУчНалСтоимПослеИзм = Выборка.Сумма + Выборка.СуммаНДС;
	КонецЕсли;

	// Проверяем изменилась ли строка в табличной части
	КоличествоИзменилось    = НЕ Выборка.КоличествоДоИзменения = Выборка.Количество;
	СуммаСНалогомИзменилась = НЕ СтТовУчНалСтоимДоИзм = СтТовУчНалСтоимПослеИзм;
	СуммаНалогаИзменилась   = НЕ Выборка.СуммаНДСДоИзменения = Выборка.СуммаНДС;
	Если КоличествоИзменилось = Ложь
		 И СуммаСНалогомИзменилась = Ложь
		 И СуммаНалогаИзменилась = Ложь Тогда
	     Продолжить;
	КонецЕсли;

	НоваяПозиция = Результат.Добавить();
	НоваяПозиция.Ссылка  = Выборка.Ссылка;

	НоваяПозиция.НаимТов = СокрЛП(Выборка.НаименованиеНоменклатуры);
	НоваяПозиция.КодТов  = Выборка.ШтрихкодНоменклатурыКонтрагента;

	КодПроисх   = ВРег(СокрЛП(Выборка.КодПроисх));
	КодПроисх   = ?(ПустаяСтрока(КодПроисх), "643", КодПроисх);
	НоваяПозиция.КодПроисх = КодПроисх;

	КрНаимСтрПр = ВРег(СокрЛП(Выборка.КрНаимСтрПр));
	КрНаимСтрПр = ?(ПустаяСтрока(КрНаимСтрПр), "RU", КрНаимСтрПр);
	НоваяПозиция.КрНаимСтрПр = КрНаимСтрПр;

	Если Выборка.СтавкаНДСДоИзменения = Перечисления.СтавкиНДС.БезНДС Тогда
		НалСтДо = "без НДС";
	ИначеЕсли Выборка.СтавкаНДСДоИзменения = Перечисления.СтавкиНДС.НДС0 Тогда
		НалСтДо = "0%";
	ИначеЕсли Выборка.СтавкаНДСДоИзменения = Перечисления.СтавкиНДС.НДС10 Тогда
		НалСтДо = "10%";
	ИначеЕсли Выборка.СтавкаНДСДоИзменения = Перечисления.СтавкиНДС.НДС10_110 Тогда
		НалСтДо = "10/110";
	ИначеЕсли Выборка.СтавкаНДСДоИзменения = Перечисления.СтавкиНДС.НДС18 Тогда
		НалСтДо = "18%";
	ИначеЕсли Выборка.СтавкаНДСДоИзменения = Перечисления.СтавкиНДС.НДС18_118 Тогда
		НалСтДо = "18/118";
	ИначеЕсли Выборка.СтавкаНДСДоИзменения = Перечисления.СтавкиНДС.НДС20  Тогда
		НалСтДо = "20%";
	ИначеЕсли Выборка.СтавкаНДСДоИзменения = Перечисления.СтавкиНДС.НДС20_120 Тогда
		НалСтДо = "20/120";
	Иначе
		НалСтДо = "НДС исчисляется налоговым агентом";
	КонецЕсли;

	Если Выборка.СтавкаНДС = Перечисления.СтавкиНДС.БезНДС Тогда
		НалСтПосле = "без НДС";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС10 Тогда
		НалСтПосле = "10%";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС10_110 Тогда
		НалСтПосле = "10/110";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС18 Тогда
		НалСтПосле = "18%";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС18_118 Тогда
		НалСтПосле = "18/118";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС20 Тогда
		НалСтПосле = "20%";
	ИначеЕсли Выборка.СтавкаНДС = Перечисления.СтавкиНДС.НДС20_120 Тогда
		НалСтПосле = "20/120";
	Иначе
		НалСтПосле = "НДС исчисляется налоговым агентом";
	КонецЕсли;

	Если Выборка.СуммаНДСДоИзменения <> 0 Тогда
		НоваяПозиция.СумНалДо = Формат(Выборка.СуммаНДСДоИзменения, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	Иначе
		НоваяПозиция.СумНалДоБезНДС = "без НДС";
	КонецЕсли;

	Если Выборка.СуммаНДС <> 0 Тогда
		НоваяПозиция.СумНалПосле = Формат(Выборка.СуммаНДС, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	Иначе
		НоваяПозиция.СумНалПослеБезНДС = "без НДС";
	КонецЕсли;

	СтТовБезНДСРазница = СтТовБезНДССтоимПослеИзм - СтТовБезНДССтоимДоИзм;
	Если СтТовБезНДСРазница <= 0 Тогда
		НоваяПозиция.СтТовБезНДССтоимУм = Формат(-СтТовБезНДСРазница, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	Иначе
		НоваяПозиция.СтТовБезНДССтоимУвел = Формат(СтТовБезНДСРазница, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	КонецЕсли;

	СуммаНДСРазница = Выборка.СуммаНДС - Выборка.СуммаНДСДоИзменения;
	Если СуммаНДСРазница <= 0 Тогда
		НоваяПозиция.СумНалРазнСумУм = Формат(-СуммаНДСРазница, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	Иначе
		НоваяПозиция.СумНалРазнСумУвел = Формат(СуммаНДСРазница, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	КонецЕсли;

	СтТовУчНалРазница  = СтТовУчНалСтоимПослеИзм - СтТовУчНалСтоимДоИзм;
	Если СтТовУчНалРазница <= 0 Тогда
		НоваяПозиция.СтТовУчНалСтоимУм = Формат(-СтТовУчНалРазница, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	Иначе
		НоваяПозиция.СтТовУчНалСтоимУвел = Формат(СтТовУчНалРазница, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	КонецЕсли;

	// Данные для ИнфПолФХЖ2
	ЭКОМ_ИнфоПоле       = Выборка.ЭКОМ_ИнфоПоля;
	НомерНакладной      = СокрЛП(Выборка.НомерИзначальнойРеализации);
	КодТовараПоставщика = СокрЛП(Выборка.КодНоменклатурыПоставщика);
	КодТовараПокупателя = СокрЛП(Выборка.КодНоменклатурыКонтрагента);
	Штрихкод 			= СокрЛП(Выборка.ШтрихкодНоменклатурыКонтрагента);
	Вложенность_океи    = СокрЛП(Выборка.НаименованиеЕдиницыИзмерения);

	ДанныеИнфополя = Новый Соответствие;
	ДанныеИнфоПоля.Вставить("CAMPAIGNNUMBER"               , Выборка.CAMPAIGNNUMBER);
	ДанныеИнфоПоля.Вставить("дата_накладной"               , Выборка.ДатаДокументаОснования);
	ДанныеИнфоПоля.Вставить("номер_уведомления_об_отгрузке", НомерНакладной);
	ДанныеИнфоПоля.Вставить("номер_накладной"              , НомерНакладной);
	ДанныеИнфоПоля.Вставить("номер_отгрузки"               , НомерНакладной);
	ДанныеИнфоПоля.Вставить("номер_акта"                   , Выборка.НомерRECADV);
	ДанныеИнфоПоля.Вставить("дата_акта"                    , Выборка.ДатаRECADV);
	ДанныеИнфоПоля.Вставить("номер_заказа"                 , Выборка.ЗаказНомер);
	ДанныеИнфоПоля.Вставить("дата_заказа"                  , Выборка.ЗаказДата);
	ДанныеИнфоПоля.Вставить("код_материала"                , КодТовараПокупателя);
	ДанныеИнфоПоля.Вставить("штрихкод"                     , Штрихкод);
	ДанныеИнфоПоля.Вставить("артикул_поставщика"           , КодТовараПоставщика);
	ДанныеИнфоПоля.Вставить("вложенность_кол"              , Выборка.КоличествоМест);
	ДанныеИнфоПоля.Вставить("вложенность_океи"             , Вложенность_океи);
	ДанныеИнфоПоля.Вставить("позиция_заказа"               , Строка(Выборка.НомерСтроки));
	ДанныеИнфоПоля.Вставить("код_продавца"                 , КодТовараПоставщика);
	ДанныеИнфоПоля.Вставить("КдВЕРНЫЙ"                     , КодТовараПокупателя);
	ДанныеИнфоПоля.Вставить("SKU"                          , КодТовараПокупателя);
	ДанныеИнфоПоля.Вставить("Штрих-код"                    , Штрихкод);

	СоответсвияИнфоПолей = ПолучитьСтруктуруИнфоПолейИзСправочника("ИнфПолФХЖ2", ЭКОМ_ИнфоПоле, ДанныеИнфополя, Выборка.Контрагент_ЭСФБезИнфПоля, ЛогСобытий);
	Если СоответсвияИнфоПолей = Неопределено Тогда
		ТекстЛогаСобытий = "Не заполнены обязательные инфо поля <<<ИнфПолФХЖ2>>>! Формат ЮЗД - " + Выборка.ФорматУПД;
		ЗаписатьЛогСобытий(ЛогСобытий, ТекстЛогаСобытий);
		Результат.Удалить(НоваяПозиция);
		Отказ = Истина;
		Продолжить;
	КонецЕсли;
	НоваяПозиция.ИнфПолСтр          = СоответсвияИнфоПолей;

	НоваяПозиция.НомСтр                   = Формат(НомерСтрокиПоПорядку, "ЧГ=0");
	НоваяПозиция.НалСтДо                  = НалСтДо;
	НоваяПозиция.НалСтПосле               = НалСтПосле;
	НоваяПозиция.ОКЕИ_Тов                 = СокрЛП(Выборка.ОКЕИ_Тов);
	НоваяПозиция.КолТовДо                 = Формат(Выборка.КоличествоДоИзменения, "ЧДЦ=3; ЧРД=.; ЧГ=0; ЧН=");
	НоваяПозиция.КолТовПосле              = Формат(Выборка.Количество, "ЧДЦ=3; ЧРД=.; ЧГ=0; ЧН=");
	НоваяПозиция.СтТовБезНДССтоимДоИзм    = Формат(СтТовБезНДССтоимДоИзм, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	НоваяПозиция.СтТовБезНДССтоимПослеИзм = Формат(СтТовБезНДССтоимПослеИзм, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	НоваяПозиция.СтТовУчНалСтоимДоИзм     = Формат(СтТовУчНалСтоимДоИзм, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	НоваяПозиция.СтТовУчНалСтоимПослеИзм  = Формат(СтТовУчНалСтоимПослеИзм, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");

	НоваяПозиция.ЦенаТовПосле       = Формат(ЦенаБезНДСПосле, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	НоваяПозиция.ЦенаТовДо          = Формат(ЦенаБезНДСДо, "ЧДЦ=2; ЧРД=.; ЧН=0.00; ЧГ=0");
	НоваяПозиция.НаимЕдИзм          = Вложенность_океи;
	НоваяПозиция.АкцизДоБезАкциз    = "без акциза";
	НоваяПозиция.АкцизПослеБезАкциз = "без акциза";
	НоваяПозиция.АкцизРазнСумУм     = "0.00";
	НоваяПозиция.НомГТД             = Выборка.НомерГТД;

	НомерСтрокиПоПорядку = НомерСтрокиПоПорядку + 1;
КонецЦикла;

Возврат Результат;
